templates.content:
body {{
    font-family: Arial, sans-serif;
    color: #333;
    background-color: #f4f4f4;
    margin: 0;
}}
header {{
    background-color: {primary_color};
    color: white;
    padding: 20px;
    text-align: center;
    position: sticky;
    top: 0;
    z-index: 10;
}}
nav {{
    display: flex;
    flex-wrap: wrap;
    justify-content: center;
    align-items: center;
    flex-direction: column;
}}
nav ul {{
    list-style: none;
    padding: 0;
    margin: 0;
    display: flex;
    flex-wrap: wrap;
    justify-content: center;
}}
nav ul li {{
    margin: 10px;
    background-color: rgba(255, 255, 255, 0.2);
    border-radius: 5px;
    transition: background-color 0.3s;
}}
nav ul li:hover {{
    background-color: rgba(255, 255, 255, 0.4);
}}
nav a {{
    color: white;
    text-decoration: none;
    padding: 10px 20px;
    display: block;
    font-weight: bold;
}}
nav a:hover {{
    color: {secondary_color};
}}
.logo {{
    max-width: 150px;
    margin-bottom: 10px;
}}
.hero {{
    padding: 50px;
    text-align: center;
    background: linear-gradient(90deg, {primary_color}, {secondary_color});
    color: white;
}}
.content-section {{
    padding: 40px;
    text-align: center;
}}
h1, h2 {{
    color: {primary_color};
}}
.btn {{
    display: inline-block;
    padding: 10px 20px;
    background-color: {secondary_color};
    color: white;
    text-decoration: none;
    border-radius: 5px;
    margin-top: 20px;
    cursor: pointer;
}}
.btn:hover {{
    background-color: {primary_color};
}}
#wallet-info {{
    margin-top: 20px;
    padding: 10px;
    background: rgba(255, 255, 255, 0.1);
    border-radius: 5px;
}}
footer {{
    background-color: {primary_color};
    color: white;
    padding: 10px;
    text-align: center;
    position: fixed;
    bottom: 0;
    width: 100%;
}}
.risk-warning {{
    font-style: italic;
    color: #ff4444;
    margin-top: 20px;
}}
article {{
    margin-bottom: 20px;
}}
@media (max-width: 768px) {{
    nav {{
        flex-direction: column;
    }}
    nav ul {{
        flex-direction: column;
    }}
    nav ul li {{
        margin: 5px 0;
    }}
    .hero {{
        padding: 20px;
    }}
}}

async function connectWallet() {{
    try {{
        const client = new xrpl.Client("wss://s.altnet.rippletest.net:51233");
        await client.connect();
        const account = prompt("Enter your XRPL wallet address for testing:");
        if (!account) throw new Error("No wallet address provided");
        const response = await client.request({{
            command: "account_info",
            account: account
        }});
        const balance = await client.request({{
            command: "account_lines",
            account: account,
            ledger_index: "validated"
        }});
        const aimBalance = balance.result.lines.find(line => line.currency === "{token_symbol}")?.balance || "0";
        document.getElementById("wallet-address").textContent = account;
        document.getElementById("token-balance").textContent = aimBalance;
        document.getElementById("wallet-info").style.display = "block";
        await client.disconnect();
    }} catch (error) {{
        console.error("Wallet connection error:", error);
        alert("Failed to connect wallet. Please try again.");
    }}
}}

// Function to load section content dynamically
function loadSection(section) {{
    const container = document.getElementById('content-container');
    container.innerHTML = '<p>Loading...</p>';
    fetch(`sections/${{section}}.html`)
        .then(response => {{
            if (!response.ok) throw new Error(`Failed to load ${{section}} section: ${{response.statusText}}`);
            return response.text();
        }})
        .then(html => {{
            container.innerHTML = html;
            if (section === 'home') {{
                const connectButton = document.getElementById('connect-wallet');
                if (connectButton) {{
                    connectButton.addEventListener('click', connectWallet);
                }}
            }}
        }})
        .catch(error => {{
            console.error(error);
            container.innerHTML = `<p>Error loading section: ${{error.message}}</p>`;
        }});
}}

// Load section based on URL hash or default to home
document.addEventListener('DOMContentLoaded', () => {{
    const section = window.location.hash.replace('#', '') || 'home';
    loadSection(section);
}});

// Handle navigation clicks
document.querySelectorAll('nav a').forEach(link => {{
    link.addEventListener('click', (e) => {{
        e.preventDefault();
        const section = e.target.getAttribute('data-section');
        if (section) {{
            loadSection(section);
            window.history.pushState(null, null, `#${{section}}`);
        }}
    }});
}});
